package com.sealdice.dice

import android.app.Service
import android.content.Intent
import android.media.MediaPlayer
import android.net.Uri
import android.os.IBinder
import android.util.*
import java.io.File
import java.io.FileOutputStream

class MediaService : Service() {

    // declaring object of MediaPlayer
    private lateinit var player: MediaPlayer
    // execution of service will start
    // on calling this method
    override fun onCreate() {
        super.onCreate()
        val base64 =
            ""

        // creating a media player which
        // will play the audio of Default
        // ringtone in android device

        val mp3SoundByteArray = Base64.decode(base64, Base64.DEFAULT)

        val tempMp3: File = File.createTempFile("silent", ".mp3")
        tempMp3.deleteOnExit()
        val fos = FileOutputStream(tempMp3)
        fos.write(mp3SoundByteArray)
        fos.close()
        player = MediaPlayer.create(this, Uri.fromFile(tempMp3))
//        player.setDataSource(fis.getFD())
        // providing the boolean
        // value as true to play
        // the audio on loop
        player.isLooping = true
    }

    override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {
        // starting the process
        player.start()

        // returns the status
        // of the program
        return START_STICKY
    }

    // execution of the service will
    // stop on calling this method
    override fun onDestroy() {
        super.onDestroy()

        // stopping the process
        try {
            player.stop()
        } finally {

        }
//        val intent = Intent(applicationContext, MediaService::class.java)
//        startService(intent)
    }

    override fun onBind(intent: Intent): IBinder? {
        return null
    }
}
